import React, { useState, useEffect } from 'react';
import { Menu, X, Brain, Calculator, BookOpen, Lightbulb, Settings, Wifi, WifiOff, Sparkles, TrendingUp, Award, Zap } from 'lucide-react';
import { Button } from '../components/Button';
import { cn } from '../lib/utils';
import { ApiService } from '../services/api';

const Navigation = ({ currentView, onViewChange }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [isChecking, setIsChecking] = useState(true);

  const menuItems = [
    { id: 'solver', label: 'Problem Solver', icon: Brain },
    { id: 'explainer', label: 'Concept Explainer', icon: Lightbulb },
    { id: 'formulas', label: 'Formula Reference', icon: Calculator },
    { id: 'study', label: 'Study Tips', icon: BookOpen },
    { id: 'settings', label: 'Settings', icon: Settings },
  ];

  useEffect(() => {
    checkConnection();
    const interval = setInterval(checkConnection, 10000); // Check every 10 seconds
    return () => clearInterval(interval);
  }, []);

  const checkConnection = async () => {
    try {
      await ApiService.healthCheck();
      setIsConnected(true);
    } catch (error) {
      setIsConnected(false);
    } finally {
      setIsChecking(false);
    }
  };

  const handleViewChange = (viewId) => {
    onViewChange(viewId);
    setIsOpen(false);
  };

  const ConnectionStatus = () => (
    <div className={cn(
      "flex items-center space-x-2 px-3 py-2 rounded-lg text-xs font-medium transition-all",
      isChecking 
        ? "bg-yellow-50 text-yellow-700 border border-yellow-200" 
        : isConnected 
          ? "bg-green-50 text-green-700 border border-green-200" 
          : "bg-red-50 text-red-700 border border-red-200"
    )}>
      {isChecking ? (
        <>
          <div className="w-2 h-2 bg-yellow-500 rounded-full animate-pulse"></div>
          <span>Checking...</span>
        </>
      ) : isConnected ? (
        <>
          <Wifi className="h-3 w-3" />
          <span>Connected</span>
        </>
      ) : (
        <>
          <WifiOff className="h-3 w-3" />
          <span>Disconnected</span>
        </>
      )}
    </div>
  );

  return (
    <>
      {/* Mobile menu button */}
      <div className="lg:hidden fixed top-4 left-4 z-50">
        <Button
          variant="outline"
          size="icon"
          onClick={() => setIsOpen(!isOpen)}
          className="glass shadow-medium"
        >
          {isOpen ? <X className="h-4 w-4" /> : <Menu className="h-4 w-4" />}
        </Button>
      </div>

      {/* Connection Status - Mobile Top Bar */}
      <div className="lg:hidden fixed top-4 right-4 z-50">
        <ConnectionStatus />
      </div>

      {/* Overlay */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-40 lg:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div className={cn(
        "fixed left-0 top-0 h-full w-72 bg-background/95 backdrop-blur-xl border-r border-border z-40 transform transition-all duration-300 ease-in-out shadow-strong",
        isOpen ? "translate-x-0" : "-translate-x-full",
        "lg:translate-x-0 lg:relative lg:z-0"
      )}>
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="p-6 border-b border-border">
            <div className="flex items-center space-x-3 mb-4">
              <div className="w-10 h-10 gradient-bg rounded-xl flex items-center justify-center shadow-medium">
                <Brain className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold gradient-text">
                  STEM Solver
                </h1>
                <p className="text-xs text-muted-foreground">AI-Powered Learning</p>
              </div>
            </div>
            
            {/* Connection Status - Desktop */}
            <div className="hidden lg:block">
              <ConnectionStatus />
            </div>
          </div>

          {/* Navigation */}
          <nav className="flex-1 p-4 space-y-2">
            {menuItems.map((item) => {
              const Icon = item.icon;
              const isActive = currentView === item.id;

              return (
                <button
                  key={item.id}
                  onClick={() => handleViewChange(item.id)}
                  className={cn(
                    "w-full flex items-center space-x-3 px-4 py-3 rounded-xl transition-all duration-200 group nav-item",
                    isActive
                      ? "bg-primary text-primary-foreground shadow-medium gradient-bg"
                      : "text-muted-foreground hover:text-foreground hover:bg-accent/50 card-hover"
                  )}
                >
                  <Icon className={cn(
                    "h-5 w-5 transition-transform group-hover:scale-110",
                    isActive && "text-white"
                  )} />
                  <span className="font-medium">{item.label}</span>
                  {isActive && (
                    <div className="ml-auto w-2 h-2 bg-white rounded-full animate-pulse"></div>
                  )}
                </button>
              );
            })}
          </nav>

          {/* Footer */}
          <div className="p-6 border-t border-border">
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-slate-800 dark:to-blue-900 rounded-xl p-4 border border-border">
              <div className="flex items-center space-x-2 mb-2">
                <div className="w-6 h-6 gradient-bg rounded-lg flex items-center justify-center">
                  <Brain className="h-3 w-3 text-white" />
                </div>
                <span className="text-sm font-medium text-foreground">Powered by Mistral AI</span>
              </div>
              <p className="text-xs text-muted-foreground leading-relaxed">
                Advanced AI technology for enhanced STEM learning and problem-solving.
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Navigation;
