import React, { useState } from 'react';
import Navigation from './navigation/Navigation';
import ProblemSolverView from './views/ProblemSolverView';
import ConceptExplainerView from './views/ConceptExplainerView';
import FormulaReferenceView from './views/FormulaReferenceView';
import StudyTipsView from './views/StudyTipsView';
import SettingsView from './views/SettingsView';
import './index.css';

function App() {
  const [currentView, setCurrentView] = useState('solver');

  const renderCurrentView = () => {
    switch (currentView) {
      case 'solver':
        return <ProblemSolverView />;
      case 'explainer':
        return <ConceptExplainerView />;
      case 'formulas':
        return <FormulaReferenceView />;
      case 'study':
        return <StudyTipsView />;
      case 'settings':
        return <SettingsView />;
      default:
        return <ProblemSolverView />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-50 dark:from-slate-900 dark:via-slate-800 dark:to-blue-900">
      <div className="flex">
        <Navigation 
          currentView={currentView} 
          onViewChange={setCurrentView}
        />
        
        <main className="flex-1 lg:ml-0">
          <div className="container mx-auto px-4 py-8 lg:px-8 mobile-padding">
            <div className="animate-fade-in">
              {renderCurrentView()}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
