import React, { useState } from 'react';
import { Send, Loader2, Download, Share, Copy, Brain, Image, FileText, Settings, History, ChevronDown, ChevronUp, BookOpen, Target, Lightbulb, CheckCircle } from 'lucide-react';
import { Button } from '../components/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/Card';
import VoiceInputButton from '../components/VoiceInputButton';
import { ApiService } from '../services/api';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import remarkMath from 'remark-math';
import rehypeKatex from 'rehype-katex';

const ProblemSolverView = () => {
  const [problem, setProblem] = useState('');
  const [subject, setSubject] = useState('Physics');
  const [difficulty, setDifficulty] = useState('Intermediate');
  const [showSteps, setShowSteps] = useState(true);
  const [includeTheory, setIncludeTheory] = useState(true);
  const [includeDiagrams, setIncludeDiagrams] = useState(true);
  const [temperature, setTemperature] = useState(0.2);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [mode, setMode] = useState('solve'); // 'solve' or 'generate-image'
  const [activeTab, setActiveTab] = useState('problem'); // 'problem', 'solution', 'notes', 'history'
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [expandedSections, setExpandedSections] = useState({
    analysis: true,
    strategy: true,
    work: true,
    result: true
  });

  const subjects = [
    'Physics', 'Chemistry', 'Mathematics', 'Biology', 'Computer Science'
  ];

  const difficultyLevels = [
    'Beginner', 'Intermediate', 'Advanced', 'University', 'Graduate'
  ];

  const handleVoiceTranscription = (transcription) => {
    setProblem(transcription);
    // Auto-solve after transcription
    setTimeout(() => {
      handleSolve();
    }, 500);
  };

  const handleSolve = async () => {
    if (!problem.trim()) {
      setError('Please enter a problem to solve');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      if (mode === 'generate-image') {
        // Generate image using DALL-E
        const response = await fetch('http://localhost:5000/api/generate-image', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prompt: problem,
            context: `Educational ${subject} content`,
            size: '512x512',
            quality: 'standard',
            style: 'educational'
          }),
        });

        const data = await response.json();

        if (data.success) {
          setSolution({
            solution: `Generated Image: ${problem}`,
            imageUrl: data.image_url,
            model: data.model,
            revisedPrompt: data.enhanced_prompt || data.revised_prompt,
            metadata: {
              ...data.metadata,
              type: 'image',
              subject: subject,
              difficulty: difficulty,
              processing_time: data.processing_time
            }
          });
        } else {
          setError(data.error || 'Failed to generate image');
        }
      } else {
        // Regular problem solving
        const response = await ApiService.solveProblem({
          problem,
          subject,
          difficulty,
          showSteps,
          includeTheory,
          includeDiagrams,
          temperature,
        });

        setSolution(response);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    if (solution?.solution) {
      if (solution.metadata?.type === 'image') {
        // For images, copy the image description and details
        const imageDetails = `Generated Image

Original Prompt: ${problem}

${solution.revisedPrompt ? `Enhanced Prompt: ${solution.revisedPrompt}` : ''}

Image URL: ${solution.imageUrl}

Model: ${solution.model}
Generated on: ${new Date(solution.metadata.timestamp).toLocaleString()}`;
        navigator.clipboard.writeText(imageDetails);
      } else {
        navigator.clipboard.writeText(solution.solution);
      }
    }
  };

  const handleDownload = () => {
    if (solution?.solution) {
      if (solution.metadata?.type === 'image') {
        // For images, download the image file
        const link = document.createElement('a');
        link.href = solution.imageUrl;
        link.download = `generated-image-${Date.now()}.png`;
        link.click();
      } else {
        // For text solutions, download as text file
        const blob = new Blob([`Problem: ${problem}\n\nSolution:\n${solution.solution}`], {
          type: 'text/plain',
        });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `solution-${Date.now()}.txt`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
    }
  };

  return (
    <div className="h-full flex flex-col space-y-6 animate-fade-in">
      <div className="flex-shrink-0 text-center space-y-2">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent">
          Problem Solver
        </h1>
        <p className="text-muted-foreground text-lg">
          Get step-by-step solutions to complex STEM problems with AI assistance
        </p>
      </div>

      <div className="flex-1 min-h-0 grid lg:grid-cols-2 gap-6">
        {/* Input Section */}
        <Card className="animate-slide-up flex flex-col">
          <CardHeader className="flex-shrink-0">
            <CardTitle className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <Send className="h-4 w-4 text-white" />
              </div>
              <span>Problem Details</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="flex-1 min-h-0 overflow-y-auto custom-scrollbar space-y-4 pr-2">
            {/* Mode Selector */}
        <div className="bg-white/90 dark:bg-gray-800/90 backdrop-blur rounded-lg p-6 shadow-lg border border-gray-200/50 dark:border-gray-700/50">
          <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">
            Select Mode
          </h3>
          <div className="flex space-x-4">
            <button
              onClick={() => setMode('solve')}
              className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
                mode === 'solve'
                  ? 'bg-blue-600 text-white shadow-lg'
                  : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600'
              }`}
            >
              <Brain size={20} />
              <span>Solve Problem</span>
            </button>
            <button
              onClick={() => setMode('generate-image')}
              className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
                mode === 'generate-image'
                  ? 'bg-purple-600 text-white shadow-lg'
                  : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600'
              }`}
            >
              <Image size={20} />
              <span>Generate Image</span>
            </button>
          </div>
        </div>

        {/* Subject Selection */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Subject</label>
              <select
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              >
                {subjects.map((s) => (
                  <option key={s} value={s}>{s}</option>
                ))}
              </select>
            </div>

            {/* Difficulty Selection */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Difficulty Level</label>
              <select
                value={difficulty}
                onChange={(e) => setDifficulty(e.target.value)}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              >
                {difficultyLevels.map((d) => (
                  <option key={d} value={d}>{d}</option>
                ))}
              </select>
            </div>

            {/* Problem Input */}
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <label className="text-sm font-medium">
                  {mode === 'generate-image' ? 'Image Generation Prompt' : 'Problem Statement'}
                </label>
                <VoiceInputButton 
                  onTranscription={handleVoiceTranscription}
                  isLoading={loading}
                />
              </div>
              <textarea
                value={problem}
                onChange={(e) => setProblem(e.target.value)}
                placeholder={mode === 'generate-image' 
                  ? "Describe the image you want to generate (e.g., 'A mathematical diagram showing the relationship between sine and cosine functions') or use the microphone to speak your prompt"
                  : "Enter your problem here or use the microphone to speak your problem..."
                }
                rows={6}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all resize-none"
              />
            </div>

            {/* Options */}
            <div className="space-y-3">
              <h4 className="text-sm font-medium">Solution Options</h4>
              <div className="space-y-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={showSteps}
                    onChange={(e) => setShowSteps(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Show detailed steps</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={includeTheory}
                    onChange={(e) => setIncludeTheory(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Include theoretical explanations</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={includeDiagrams}
                    onChange={(e) => setIncludeDiagrams(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Suggest diagrams where applicable</span>
                </label>
              </div>
            </div>

            {/* Temperature Slider */}
            <div className="space-y-2">
              <label className="text-sm font-medium">
                Model Temperature: {temperature}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={temperature}
                onChange={(e) => setTemperature(parseFloat(e.target.value))}
                className="w-full h-2 bg-secondary rounded-lg appearance-none cursor-pointer"
              />
              <div className="flex justify-between text-xs text-muted-foreground">
                <span>More focused</span>
                <span>More creative</span>
              </div>
            </div>

            {/* Solve Button */}
            <Button
              onClick={handleSolve}
              disabled={loading || !problem.trim()}
              className={`w-full h-12 text-lg transition-all duration-200 shadow-lg ${
                mode === 'generate-image'
                  ? 'bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 shadow-purple-500/25'
                  : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 shadow-blue-500/25'
              }`}
            >
              {loading ? (
                <>
                  <Loader2 className="h-5 w-5 mr-2 animate-spin" />
                  {mode === 'generate-image' ? 'Generating...' : 'Solving...'}
                </>
              ) : (
                <>
                  {mode === 'generate-image' ? (
                    <>
                      <Image className="h-5 w-5 mr-2" />
                      Generate Image
                    </>
                  ) : (
                    <>
                      <Send className="h-5 w-5 mr-2" />
                      Solve Problem
                    </>
                  )}
                </>
              )}
            </Button>

            {error && (
              <div className="p-4 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg text-red-600 dark:text-red-400 text-sm">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-red-500 rounded-full"></div>
                  <span className="font-medium">Error</span>
                </div>
                <p className="mt-1">{error}</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Solution Section */}
        <Card className="animate-slide-up flex flex-col">
          <CardHeader className="flex-shrink-0">
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center space-x-2">
                <Brain className="h-5 w-5 text-primary" />
                <span>Solution</span>
              </CardTitle>
              {solution && (
                <div className="flex space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleCopy}
                    className="hover:bg-primary/10"
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleDownload}
                    className="hover:bg-primary/10"
                  >
                    <Download className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    className="hover:bg-primary/10"
                  >
                    <Share className="h-4 w-4" />
                  </Button>
                </div>
              )}
            </div>
          </CardHeader>
          <CardContent className="flex-1 min-h-0">
            {solution ? (
              <div className="h-full flex flex-col space-y-4">
                <div className="flex-shrink-0 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-slate-800 dark:to-blue-900 rounded-lg border border-blue-200/50 dark:border-blue-800/50">
                  <div className="text-sm text-muted-foreground mb-2 flex items-center space-x-2">
                    <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                    <span>
                      {solution.metadata?.type === 'image' ? 'Generated' : 'Solved'} on {new Date(solution.metadata.timestamp).toLocaleString()}
                    </span>
                  </div>
                  <div className="flex flex-wrap gap-2 text-sm">
                    <span className="bg-primary/10 text-primary px-3 py-1 rounded-full font-medium border border-primary/20">
                      {solution.metadata.subject}
                    </span>
                    <span className="bg-secondary/10 text-secondary-foreground px-3 py-1 rounded-full font-medium border border-secondary/20">
                      {solution.metadata?.type === 'image' ? 'AI Generated Image' : solution.metadata.difficulty}
                    </span>
                    {solution.model && (
                      <span className="bg-purple-100 dark:bg-purple-900/30 text-purple-700 dark:text-purple-300 px-3 py-1 rounded-full font-medium border border-purple-200 dark:border-purple-800">
                        {solution.model}
                      </span>
                    )}
                  </div>
                </div>
                <div className="flex-1 min-h-0 overflow-y-auto custom-scrollbar">
                  {solution.metadata?.type === 'image' ? (
                    <div className="space-y-4">
                      {/* Generated Image Display */}
                      <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                        <img 
                          src={solution.imageUrl} 
                          alt={solution.revisedPrompt || problem}
                          className="w-full max-w-2xl mx-auto rounded-lg shadow-lg"
                          style={{ maxHeight: '500px', objectFit: 'contain' }}
                        />
                      </div>
                      
                      {/* Image Details */}
                      <div className="space-y-3">
                        <div className="bg-gray-50 dark:bg-gray-800/50 rounded-lg p-4">
                          <h4 className="font-semibold text-gray-800 dark:text-white mb-2">Original Prompt:</h4>
                          <p className="text-gray-700 dark:text-gray-300">{problem}</p>
                        </div>
                        
                        {solution.revisedPrompt && solution.revisedPrompt !== problem && (
                          <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-4 border border-blue-200 dark:border-blue-800">
                            <h4 className="font-semibold text-blue-800 dark:text-blue-200 mb-2">Enhanced Prompt:</h4>
                            <p className="text-blue-700 dark:text-blue-300">{solution.revisedPrompt}</p>
                          </div>
                        )}
                        
                        {solution.metadata && (
                          <div className="bg-green-50 dark:bg-green-900/20 rounded-lg p-4 border border-green-200 dark:border-green-800">
                            <h4 className="font-semibold text-green-800 dark:text-green-200 mb-2">Generation Details:</h4>
                            <div className="grid grid-cols-2 gap-4 text-sm">
                              <div>
                                <span className="text-green-700 dark:text-green-300 font-medium">Size:</span>
                                <span className="ml-2 text-green-600 dark:text-green-400">{solution.metadata.size || 'N/A'}</span>
                              </div>
                              <div>
                                <span className="text-green-700 dark:text-green-300 font-medium">Quality:</span>
                                <span className="ml-2 text-green-600 dark:text-green-400">{solution.metadata.quality || 'N/A'}</span>
                              </div>
                              <div>
                                <span className="text-green-700 dark:text-green-300 font-medium">Style:</span>
                                <span className="ml-2 text-green-600 dark:text-green-400">{solution.metadata.style || 'N/A'}</span>
                              </div>
                              <div>
                                <span className="text-green-700 dark:text-green-300 font-medium">Subject:</span>
                                <span className="ml-2 text-green-600 dark:text-green-400">{solution.metadata.subject || 'N/A'}</span>
                              </div>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  ) : (
                    <div className="prose prose-sm max-w-none dark:prose-invert markdown-content pr-2">
                      <ReactMarkdown
                        remarkPlugins={[remarkGfm, remarkMath]}
                        rehypePlugins={[rehypeKatex]}
                      >
                        {solution.solution}
                      </ReactMarkdown>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div className="h-full flex items-center justify-center text-center py-12 text-muted-foreground">
                <div className="space-y-4">
                  <div className="relative">
                    {mode === 'generate-image' ? (
                      <Image className="h-16 w-16 mx-auto opacity-20" />
                    ) : (
                      <Brain className="h-16 w-16 mx-auto opacity-20" />
                    )}
                    <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-full blur-xl"></div>
                  </div>
                  <div>
                    <p className="text-lg font-medium text-foreground mb-2">
                      {mode === 'generate-image' ? 'Ready to generate images' : 'Ready to solve problems'}
                    </p>
                    <p className="text-sm">
                      {mode === 'generate-image' 
                        ? 'Enter an image description and click "Generate Image" to create AI-powered visuals'
                        : 'Enter a problem and click "Solve Problem" to get started with AI-powered solutions'
                      }
                    </p>
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default ProblemSolverView;
