import React, { useState, useEffect } from 'react';
import Navigation from './navigation/Navigation';
import ProblemSolverView from './views/ProblemSolverView';
import ConceptExplainerView from './views/ConceptExplainerView';
import FormulaReferenceView from './views/FormulaReferenceView';
import StudyTipsView from './views/StudyTipsView';
import SettingsView from './views/SettingsView';
import { ApiService } from './services/api';
import './index.css';

function App() {
  const [currentView, setCurrentView] = useState('solver');
  const [apiStatus, setApiStatus] = useState('checking');

  useEffect(() => {
    // Check API health on startup
    const checkApiHealth = async () => {
      try {
        await ApiService.healthCheck();
        setApiStatus('connected');
      } catch (error) {
        setApiStatus('disconnected');
        console.error('API health check failed:', error);
      }
    };

    checkApiHealth();
  }, []);

  const renderCurrentView = () => {
    switch (currentView) {
      case 'solver':
        return <ProblemSolverView />;
      case 'explainer':
        return <ConceptExplainerView />;
      case 'formulas':
        return <FormulaReferenceView />;
      case 'study':
        return <StudyTipsView />;
      case 'settings':
        return <SettingsView />;
      default:
        return <ProblemSolverView />;
    }
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Background Gradient */}
      <div className="fixed inset-0 bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 -z-10" />
      
      <div className="flex">
        <Navigation 
          currentView={currentView} 
          onViewChange={setCurrentView}
          apiStatus={apiStatus}
        />
        
        <main className="flex-1 lg:ml-0">
          <div className="container mx-auto px-4 py-8 lg:px-8">
            {apiStatus === 'disconnected' && (
              <div className="mb-6 p-4 bg-destructive/10 border border-destructive/20 rounded-lg">
                <p className="text-destructive text-sm">
                  ⚠️ Unable to connect to the API server. Please ensure the backend is running.
                </p>
              </div>
            )}
            
            {renderCurrentView()}
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
