import React, { useState } from 'react';
import { Send, Loader2, Download, Share, Copy } from 'lucide-react';
import { Button } from '../components/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/Card';
import { ApiService } from '../services/api';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import remarkMath from 'remark-math';
import rehypeKatex from 'rehype-katex';

const ProblemSolverView = () => {
  const [problem, setProblem] = useState('');
  const [subject, setSubject] = useState('Physics');
  const [difficulty, setDifficulty] = useState('Intermediate');
  const [showSteps, setShowSteps] = useState(true);
  const [includeTheory, setIncludeTheory] = useState(true);
  const [includeDiagrams, setIncludeDiagrams] = useState(true);
  const [temperature, setTemperature] = useState(0.2);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const subjects = [
    'Physics', 'Chemistry', 'Mathematics', 'Biology', 'Computer Science'
  ];

  const difficultyLevels = [
    'Beginner', 'Intermediate', 'Advanced', 'University', 'Graduate'
  ];

  const handleSolve = async () => {
    if (!problem.trim()) {
      setError('Please enter a problem to solve');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await ApiService.solveProblem({
        problem,
        subject,
        difficulty,
        showSteps,
        includeTheory,
        includeDiagrams,
        temperature,
      });

      setSolution(response);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    if (solution?.solution) {
      navigator.clipboard.writeText(solution.solution);
    }
  };

  const handleDownload = () => {
    if (solution?.solution) {
      const blob = new Blob([`Problem: ${problem}\n\nSolution:\n${solution.solution}`], {
        type: 'text/plain',
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `solution-${Date.now()}.txt`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }
  };

  return (
    <div className="space-y-6 animate-fade-in">
      <div className="text-center space-y-2">
        <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
          Problem Solver
        </h1>
        <p className="text-muted-foreground">
          Get step-by-step solutions to complex STEM problems
        </p>
      </div>

      <div className="grid lg:grid-cols-2 gap-6">
        {/* Input Section */}
        <Card className="animate-slide-up">
          <CardHeader>
            <CardTitle>Problem Details</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Subject Selection */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Subject</label>
              <select
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              >
                {subjects.map((s) => (
                  <option key={s} value={s}>{s}</option>
                ))}
              </select>
            </div>

            {/* Difficulty Selection */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Difficulty Level</label>
              <select
                value={difficulty}
                onChange={(e) => setDifficulty(e.target.value)}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              >
                {difficultyLevels.map((d) => (
                  <option key={d} value={d}>{d}</option>
                ))}
              </select>
            </div>

            {/* Problem Input */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Problem Statement</label>
              <textarea
                value={problem}
                onChange={(e) => setProblem(e.target.value)}
                placeholder="Enter your problem here..."
                rows={6}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all resize-none"
              />
            </div>

            {/* Options */}
            <div className="space-y-3">
              <h4 className="text-sm font-medium">Solution Options</h4>
              <div className="space-y-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={showSteps}
                    onChange={(e) => setShowSteps(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Show detailed steps</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={includeTheory}
                    onChange={(e) => setIncludeTheory(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Include theoretical explanations</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={includeDiagrams}
                    onChange={(e) => setIncludeDiagrams(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Suggest diagrams where applicable</span>
                </label>
              </div>
            </div>

            {/* Temperature Slider */}
            <div className="space-y-2">
              <label className="text-sm font-medium">
                Model Temperature: {temperature}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={temperature}
                onChange={(e) => setTemperature(parseFloat(e.target.value))}
                className="w-full h-2 bg-secondary rounded-lg appearance-none cursor-pointer"
              />
              <div className="flex justify-between text-xs text-muted-foreground">
                <span>More focused</span>
                <span>More creative</span>
              </div>
            </div>

            {/* Solve Button */}
            <Button
              onClick={handleSolve}
              disabled={loading || !problem.trim()}
              className="w-full h-12 text-lg"
            >
              {loading ? (
                <>
                  <Loader2 className="h-5 w-5 mr-2 animate-spin" />
                  Solving...
                </>
              ) : (
                <>
                  <Send className="h-5 w-5 mr-2" />
                  Solve Problem
                </>
              )}
            </Button>

            {error && (
              <div className="p-3 bg-destructive/10 border border-destructive/20 rounded-lg text-destructive text-sm">
                {error}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Solution Section */}
        <Card className="animate-slide-up">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Solution</CardTitle>
              {solution && (
                <div className="flex space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleCopy}
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleDownload}
                  >
                    <Download className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                  >
                    <Share className="h-4 w-4" />
                  </Button>
                </div>
              )}
            </div>
          </CardHeader>
          <CardContent>
            {solution ? (
              <div className="space-y-4">
                <div className="p-4 bg-muted/50 rounded-lg">
                  <div className="text-sm text-muted-foreground mb-2">
                    Solved on {new Date(solution.metadata.timestamp).toLocaleString()}
                  </div>
                  <div className="flex space-x-4 text-sm">
                    <span className="bg-primary/10 text-primary px-2 py-1 rounded">
                      {solution.metadata.subject}
                    </span>
                    <span className="bg-secondary/10 text-secondary-foreground px-2 py-1 rounded">
                      {solution.metadata.difficulty}
                    </span>
                  </div>
                </div>
                <div className="prose prose-sm max-w-none dark:prose-invert markdown-content">
                  <ReactMarkdown
                    remarkPlugins={[remarkGfm, remarkMath]}
                    rehypePlugins={[rehypeKatex]}
                  >
                    {solution.solution}
                  </ReactMarkdown>
                </div>
              </div>
            ) : (
              <div className="text-center py-12 text-muted-foreground">
                <Brain className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>Enter a problem and click "Solve Problem" to get started</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default ProblemSolverView;
