import React, { useState } from 'react';
import { Lightbulb, Loader2, Copy, Download, Share } from 'lucide-react';
import { Button } from '../components/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/Card';
import { ApiService } from '../services/api';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import remarkMath from 'remark-math';
import rehypeKatex from 'rehype-katex';

const ConceptExplainerView = () => {
  const [concept, setConcept] = useState('');
  const [subject, setSubject] = useState('Physics');
  const [level, setLevel] = useState('intermediate');
  const [includeExamples, setIncludeExamples] = useState(true);
  const [includeHistory, setIncludeHistory] = useState(false);
  const [temperature, setTemperature] = useState(0.3);
  const [explanation, setExplanation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const subjects = [
    'Physics', 'Chemistry', 'Mathematics', 'Biology', 'Computer Science'
  ];

  const levels = [
    { value: 'beginner', label: 'Beginner' },
    { value: 'intermediate', label: 'Intermediate' },
    { value: 'advanced', label: 'Advanced' }
  ];

  const handleExplain = async () => {
    if (!concept.trim()) {
      setError('Please enter a concept to explain');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await ApiService.explainConcept({
        concept,
        subject,
        level,
        includeExamples,
        includeHistory,
        temperature,
      });

      setExplanation(response);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    if (explanation?.explanation) {
      navigator.clipboard.writeText(explanation.explanation);
    }
  };

  const handleDownload = () => {
    if (explanation?.explanation) {
      const blob = new Blob([`Concept: ${concept}\n\nExplanation:\n${explanation.explanation}`], {
        type: 'text/plain',
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `concept-explanation-${Date.now()}.txt`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }
  };

  return (
    <div className="space-y-6 animate-fade-in">
      <div className="text-center space-y-2">
        <h1 className="text-3xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent">
          Concept Explainer
        </h1>
        <p className="text-muted-foreground">
          Get clear explanations of complex STEM concepts
        </p>
      </div>

      <div className="grid lg:grid-cols-2 gap-6">
        {/* Input Section */}
        <Card className="animate-slide-up">
          <CardHeader>
            <CardTitle>Concept Details</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Concept Input */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Concept</label>
              <input
                type="text"
                value={concept}
                onChange={(e) => setConcept(e.target.value)}
                placeholder="e.g., Quantum Entanglement, DNA Replication, Machine Learning"
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              />
            </div>

            {/* Subject Selection */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Subject Area</label>
              <select
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              >
                {subjects.map((s) => (
                  <option key={s} value={s}>{s}</option>
                ))}
              </select>
            </div>

            {/* Level Selection */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Explanation Level</label>
              <select
                value={level}
                onChange={(e) => setLevel(e.target.value)}
                className="w-full p-3 border border-input rounded-lg bg-background focus:ring-2 focus:ring-ring focus:border-transparent transition-all"
              >
                {levels.map((l) => (
                  <option key={l.value} value={l.value}>{l.label}</option>
                ))}
              </select>
            </div>

            {/* Options */}
            <div className="space-y-3">
              <h4 className="text-sm font-medium">Explanation Options</h4>
              <div className="space-y-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={includeExamples}
                    onChange={(e) => setIncludeExamples(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Include practical examples</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={includeHistory}
                    onChange={(e) => setIncludeHistory(e.target.checked)}
                    className="rounded border-input"
                  />
                  <span className="text-sm">Include historical context</span>
                </label>
              </div>
            </div>

            {/* Temperature Slider */}
            <div className="space-y-2">
              <label className="text-sm font-medium">
                Creativity Level: {temperature}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={temperature}
                onChange={(e) => setTemperature(parseFloat(e.target.value))}
                className="w-full h-2 bg-secondary rounded-lg appearance-none cursor-pointer"
              />
              <div className="flex justify-between text-xs text-muted-foreground">
                <span>More factual</span>
                <span>More creative</span>
              </div>
            </div>

            {/* Explain Button */}
            <Button
              onClick={handleExplain}
              disabled={loading || !concept.trim()}
              className="w-full h-12 text-lg"
            >
              {loading ? (
                <>
                  <Loader2 className="h-5 w-5 mr-2 animate-spin" />
                  Explaining...
                </>
              ) : (
                <>
                  <Lightbulb className="h-5 w-5 mr-2" />
                  Explain Concept
                </>
              )}
            </Button>

            {error && (
              <div className="p-3 bg-destructive/10 border border-destructive/20 rounded-lg text-destructive text-sm">
                {error}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Explanation Section */}
        <Card className="animate-slide-up">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Explanation</CardTitle>
              {explanation && (
                <div className="flex space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleCopy}
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleDownload}
                  >
                    <Download className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                  >
                    <Share className="h-4 w-4" />
                  </Button>
                </div>
              )}
            </div>
          </CardHeader>
          <CardContent>
            {explanation ? (
              <div className="space-y-4">
                <div className="p-4 bg-muted/50 rounded-lg">
                  <div className="text-sm text-muted-foreground mb-2">
                    Explained on {new Date(explanation.metadata.timestamp).toLocaleString()}
                  </div>
                  <div className="flex space-x-4 text-sm">
                    <span className="bg-primary/10 text-primary px-2 py-1 rounded">
                      {explanation.metadata.concept}
                    </span>
                    <span className="bg-secondary/10 text-secondary-foreground px-2 py-1 rounded">
                      {explanation.metadata.level}
                    </span>
                  </div>
                </div>
                <div className="prose prose-sm max-w-none dark:prose-invert markdown-content">
                  <ReactMarkdown
                    remarkPlugins={[remarkGfm, remarkMath]}
                    rehypePlugins={[rehypeKatex]}
                  >
                    {explanation.explanation}
                  </ReactMarkdown>
                </div>
              </div>
            ) : (
              <div className="text-center py-12 text-muted-foreground">
                <Lightbulb className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>Enter a concept and click "Explain Concept" to get started</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default ConceptExplainerView;
